import {
    // SEND_MESSAGE,
    FETCH_MESSAGES,
    // FETCH_MESSAGE,
    // DELETE_MESSAGE
} from "../actions/types";

const mailReducer = (state = {}, action) => {
    switch (action.type) {
        case FETCH_MESSAGES:
            return { ...state, [action.payload.id]: action.payload };
        // case FETCH_MESSAGE:
        //     return { ...state, [action.payload.id]: action.payload };
        // case SEND_MESSAGE:
        //     return { ...state, [action.payload.id]: action.payload };
        default:
            return state
    }
}

export default mailReducer;
