import axios from "axios";
import mails from "../apis/mails";
import history from "../history";
import {
    SIGN_IN,
    SIGN_OUT,
    // SEND_MESSAGE,
    FETCH_MESSAGES,
    // FETCH_MESSAGE,
    // DELETE_MESSAGE
} from './types';

export const signIn = (userId) => {
    return {
        type: SIGN_IN,
        payload: userId
    }
}

export const signOut = () => {
    return {
        type: SIGN_OUT
    }
}

