import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchMessages } from '../actions';

class MailList extends Component {
    componentDidMount() {
        this.props.fetchMessages()
    }

    renderAdmin(mail) {
        if (mail.userId === this.props.currentUserId) {
            return (
                <div className="right floated content">
                    <Link to={`/mails/edit/${mail.id}`} className="ui button primary">Edit</Link>
                </div>
            )
        }
    }

    renderList() {
        return this.props.mails.map(mail => {
            return (
                <div className="item" key={mail.id}>
                    {this.renderAdmin(mail)}
                    <i className="large middle aligned icon camera" />
                    <div className="content">
                        <Link to={`/mails/${mail.id}`} className="header">
                            {mail.title}
                        </Link>
                        <div className="description">{mail.description}</div>
                    </div>
                </div>
            )
        })
    }

    renderCreate() {
        if (this.props.isSignedIn) {
            return (
                <div style={{ textAlign: "right" }}>
                    <Link to="/mails/new" className="ui button primary">
                        Create mail
                    </Link>
                </div>
            )
        }
    }

    render() {
        return (
            <div>
                <h2>mails</h2>
                <div className="ui celled list">{this.renderList()}</div>
                {this.renderCreate()}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        mails: Object.values(state.mails),
        currentUserId: state.auth.userId,
        isSignedIn: state.auth.isSignedIn
    }
}

export default connect(mapStateToProps, { fetchMessages })(MailList);
